    public Connection give(){
        try{  
            Class.forName("com.mysql.jdbc.Driver");
           con = DriverManager.getConnection("jdbc:mysql://localhost/Emp","root","");
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(this,e+"conn");
        }
        
        return con;
    }
    
     public void insert(String a ,String b, int c , int d){
          try{  
             con = give();
            PreparedStatement ps= con.prepareStatement("insert into producttable values(?,?,?,?)");
            
                            ps.setString(1,a);
                            ps.setString(2,b);
                            ps.setInt(3,c);
                            ps.setInt(4,d);
          
            int q = ps.executeUpdate();
            
            if(q==1)
                JOptionPane.showMessageDialog(this,"Record added");
                     
          else{
          JOptionPane.showMessageDialog(this,"Record  not added");
                  }
                       }
        
        catch(Exception e){
                 JOptionPane.showMessageDialog(this,e+"insert");
                }
    }
     
     public void update(String pn,int q,int qadd,int price){
         try{  
            con= give();
            PreparedStatement ps = con.prepareStatement("update producttable set quantity=?,price=? where ProductNo=?");
            
            q = q+qadd;
            ps.setInt(1,q);
            ps.setInt(2,price);
            ps.setString(3,pn);
            
            int g = ps.executeUpdate();
            
            if(g==1)
                JOptionPane.showMessageDialog(this,"Record updated");
                     
          else{
          JOptionPane.showMessageDialog(this,"Record  not updated");
                  }
            
    }
         catch(Exception e){
             JOptionPane.showMessageDialog(this,e+"update");
              
         }
    }
     
     
