    private void addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActionPerformed
        if(evt.getSource()== add){
            String name = t1.getText();
            String num = t2.getText(); 
            int q = Integer.parseInt(t3.getText());
            int p = Integer.parseInt(t4.getText());
            int f=0,k=0,m=0,j=0;
          try{  
          /*  Class.forName("com.mysql.jdbc.Driver");
            Connection c = DriverManager.getConnection("jdbc:mysql://localhost/Emp","root","") ;*/
            con = give();
 
            PreparedStatement ps = con.prepareStatement("select * from producttable");
         
            ResultSet rs = ps.executeQuery();
            
            while(rs.next()){
                k=1;
                String pn = rs.getString(2);
                int qn = rs.getInt(3);
                
                 if(pn.compareTo(num)==0){
                      f= 1;
                     String n = rs.getString(1);
                     
                     if(n.compareTo(name)!=0){
                         JOptionPane.showMessageDialog(this,"Product with same number but different name already exists......Change the Product number");
                         break;
                     }
                     
                     else if(n.compareTo(name)==0){
                         update(num,qn,q,p);
                         break;
                     }
                 }
                
            }
            
            if(f==0)
            
                {
                  insert(name,num,q,p);
                       }
          }
        catch(Exception e){
                 JOptionPane.showMessageDialog(this,e);
               } 
         }
    }//GEN-LAST:event_addActionPerformed


